function load_dark_mode(){document.querySelector("html").classList.add(`theme-dark`)}function load_light_mode(){document.querySelector("html").classList.add(`theme-light`)}if(!localStorage.getItem("theme")){const a=window.matchMedia("(prefers-color-scheme: dark)");a.matches?load_dark_mode():load_light_mode()}else"dark"!=localStorage.getItem("theme")||"light"!=localStorage.getItem("theme")?localStorage.removeItem("theme"):"dark"==localStorage.getItem("theme")?load_dark_mode():load_light_mode();
